package ch03;

public class OperatorEx04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = -10; //i 는 -10
		i = +i;		//i 는 -10	(+는 곱하기 +1 것과 동일)
		System.out.println(i); // i는 10출력
		
		i = -10; // i 는 - 10
		i = -i;	// i 는 10	(+는 곱하기 -1 것과 동일)
		System.out.println(i); // i 는 10
	}

}
